function showSuccessMessage(message) {
    $('.toast').removeClass('bg-warning');
    $('.toast').removeClass('bg-danger');
    $('.toast').addClass('bg-success');
    $('.toast-msg').html('<i class="fa fa-check" aria-hidden="true"></i> ' + message);
    $('.toast').toast('show');
    $('.toast').css({
        'position': 'fixed',
        'top': '20px', // Adjust as necessary to place it at the top
        'right': '20px', // Adjust as necessary to place it on the right
        'z-index': '9999', // Ensure this is higher than modal z-index
        'min-width': '300px', // Optional: to ensure it's not too small
        'max-width': '500px' // Optional: to limit the maximum width
    });
}



