const IS_REQUIRED = function () {

    var REQUIRED_INPUTS = [];
    const clickEvent = function (buttons) {

        for (var i = 0; i < buttons.length; i++) {
            var btn = document.getElementById(buttons[i]);
            btn.addEventListener("click", function () {
                //alert('Show Modal');
            });
        }
    };



    return {
        checkRequired: function (inputs) {
            REQUIRED_INPUTS = inputs;
            for (var i = 0; i < inputs.length; i++) {
                var input = document.getElementById(inputs[i]);


                input.addEventListener("input", function () {
                    if (input.value.trim().length === 0) {
                        input.style.borderColor = "red";
                    }else{
                        input.style.borderColor = "#059669";
                    }
                });


                input.style.borderColor = "#059669";
                if (input.value.trim().length === 0) {
                    input.style.borderColor = "red";
                    input.focus();
                    return true;
                }
            }
            return false;
        },

        addRequiredListener: function (obects) {
            clickEvent(obects);
        },

        reset: function () {
            for (var i = 0; i < REQUIRED_INPUTS.length; i++) {
                var input = document.getElementById(REQUIRED_INPUTS[i]);
                input.style.borderColor = "#D1D5DB";
            }
        },
    }
}();


